upo1;}$documentodocente = 1;$query_updeliminardocentegrupo = "UPDATE grupo SET numerodocumento='$documentodocente' WHERE idgrupo = '$idgrupo'";$updeliminardocentegrupo = $db->Execute($query_updeliminardocentegrupo);echo "<script language='javascript'>    window.opener.recargar('".$dirini."#".$grupo."');    window.opener.focus();    window.close();    </script>";?>